```tsx
'use client';

import React from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Task } from '@/types';

interface TaskNavigationBarProps {
  tasks: Task[];
  currentTaskIndex: number;
  onTaskSelect: (index: number) => void;
  onSearch: (query: string) => void;
}

const TaskNavigationBar: React.FC<TaskNavigationBarProps> = ({
  tasks,
  currentTaskIndex,
  onTaskSelect,
  onSearch,
}) => {
  // Handle search input changes
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onSearch(e.target.value);
  };

  return (
    <div className="border-b border-gray-700 bg-gray-800 p-4">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          {/* Previous/Next */}
          <Button
            onClick={() => onTaskSelect(Math.max(0, currentTaskIndex - 1))}
            disabled={currentTaskIndex === 0 || tasks.length === 0}
            variant="outline"
          >
            ‚óÄÔ∏è Prev
          </Button>

          {/* Task Selector */}
          <Select
            value={tasks.length > 0 ? currentTaskIndex.toString() : ''}
            onValueChange={(value) => onTaskSelect(parseInt(value))}
            disabled={tasks.length === 0}
          >
            <SelectTrigger className="w-[300px]">
              <SelectValue placeholder="Select a task" />
            </SelectTrigger>
            <SelectContent>
              {tasks.map((task, index) => (
                <SelectItem key={task.id} value={index.toString()}>
                  Task {task.id}: {task.title.length > 40 ? `${task.title.substring(0, 40)}...` : task.title}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Button
            onClick={() => onTaskSelect(Math.min(tasks.length - 1, currentTaskIndex + 1))}
            disabled={currentTaskIndex >= tasks.length - 1 || tasks.length === 0}
            variant="outline"
          >
            ‚ñ∂Ô∏è Next
          </Button>
        </div>

        {/* Search */}
        <Input
          placeholder="üîç Search tasks..."
          className="w-[200px]"
          onChange={handleSearchChange}
        />
      </div>
    </div>
  );
};

export default TaskNavigationBar;
```

This component provides:
1. Previous and next buttons for sequential task navigation
2. A dropdown selector for direct task selection
3. A search input for filtering tasks
4. Proper handling of edge cases (empty task list, first/last task)

Create a `components/ContextSelector.tsx` file with the following implementation:

```tsx
'use client';

import React from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { TaskData } from '@/types';

interface ContextSelectorProps {
  taskData: TaskData | null;
  currentContext: string;
  onContextSelect: (context: string) => void;
}

const ContextSelector: React.FC<ContextSelectorProps> = ({
  taskData,
  currentContext,
  onContextSelect,
}) => {
  if (!taskData) {
    return null;
  }

  // Get available contexts
  const contexts = Object.keys(taskData);

  // Calculate stats for each context
  const getContextStats = (contextName: string) => {
    const contextData = taskData[contextName];
    const tasks = contextData.tasks;
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter(task => task.status === 'done').length;
    const activeTasks = tasks.filter(task => task.status === 'in-progress').length;
    const progress = totalTasks > 0 ? (completedTasks / totalTasks) * 100 : 0;

    return {
      totalTasks,
      completedTasks,
      activeTasks,
      progress,
      description: contextData.metadata.description,
      updated: new Date(contextData.metadata.updated).toLocaleString(),
    };
  };

  // Get icon for context
  const getContextIcon = (contextName: string) => {
    const icons = {
      master: 'üìÇ',
      coverage: 'üü°',
      default: 'üìã',
    };
    return icons[contextName] || icons.default;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4 bg-gray-900">
      {contexts.map(contextName => {
        const stats = getContextStats(contextName);
        const isActive = contextName === currentContext;

        return (
          <Card 
            key={contextName}
            className={`cursor-pointer hover:bg-gray-700 transition-colors ${isActive ? 'bg-gray-700 border-green-500' : 'bg-gray-800'}`}
            onClick={() => onContextSelect(contextName)}
          >
            <CardContent className="p-4">
              <div className="flex items-center justify-between mb-2">
                <h3 className="text-lg font-medium">
                  {getContextIcon(contextName)} {contextName.charAt(0).toUpperCase() + contextName.slice(1)} Context
                </h3>
                {isActive && (
                  <span className="text-xs bg-green-600 text-white px-2 py-1 rounded">Active</span>
                )}
              </div>

              <Progress value={stats.progress} className="h-2 mb-2" />

              <div className="text-sm text-gray-400">
                <span>{stats.completedTasks}/{stats.totalTasks} tasks</span>
                {stats.activeTasks > 0 && (
                  <span className="ml-2">‚Ä¢ {stats.activeTasks} active</span>
                )}
              </div>

              {stats.description && (
                <p className="text-xs text-gray-500 mt-2 truncate">{stats.description}</p>
              )}

              <div className="text-xs text-gray-500 mt-2">
                Updated: {stats.updated}
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
};

export default ContextSelector;
```

This component:
1. Displays cards for each available context in the task data
2. Shows progress information (completed/total tasks)
3. Highlights the currently active context
4. Provides visual indicators of task status within each context
5. Displays metadata like description and last updated time

Create a `components/TaskPanel.tsx` file with the following implementation:

```tsx
'use client';

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Task } from '@/types';

interface TaskPanelProps {
  task?: Task;
}

const TaskPanel: React.FC<TaskPanelProps> = ({ task }) => {
  if (!task) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <h2 className="text-xl font-bold mb-2">‚è∏Ô∏è No Active Task</h2>
          <p className="text-gray-400">Please select a task to view details</p>
        </CardContent>
      </Card>
    );
  }

  // Get appropriate status badge color
  const getStatusBadge = (status: string) => {
    const variants: Record<string, string> = {
      'done': 'bg-blue-500 hover:bg-blue-600',
      'in-progress': 'bg-green-500 hover:bg-green-600',
      'pending': 'bg-yellow-500 hover:bg-yellow-600',
      'cancelled': 'bg-red-500 hover:bg-red-600'
    };
    return variants[status.toLowerCase()] || 'bg-gray-500 hover:bg-gray-600';
  };

  // Get priority badge color
  const getPriorityBadge = (priority: string) => {
    const variants: Record<string, string> = {
      'high': 'bg-red-500 hover:bg-red-600',
      'medium': 'bg-yellow-500 hover:bg-yellow-600',
      'low': 'bg-blue-500 hover:bg-blue-600'
    };
    return variants[priority.toLowerCase()] || 'bg-gray-500 hover:bg-gray-600';
  };

  // Calculate progress if task has subtasks
  const completedSubtasks = task.subtasks?.filter(s => s.status === 'done').length || 0;
  const totalSubtasks = task.subtasks?.length || 0;
  const progress = totalSubtasks > 0 ? (completedSubtasks / totalSubtasks) * 100 : (task.progress || 0);

  // Format markdown-like content for display
  const formatContent = (content: string) => {
    // Simple markdown-like formatting
    return content
      .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.+?)\*/g, '<em>$1</em>')
      .replace(/```([\s\S]+?)```/g, '<pre class="bg-gray-900 p-3 rounded my-2 overflow-x-auto">$1</pre>')
      .replace(/`([^`]+)`/g, '<code class="bg-gray-900 px-1 rounded">$1</code>')
      .split('\n').map((line, i) => {
        // Handle headers
        if (line.startsWith('# ')) {
          return `<h1 class="text-xl font-bold my-2">${line.substring(2)}</h1>`;
        }
        if (line.startsWith('## ')) {
          return `<h2 class="text-lg font-bold my-2">${line.substring(3)}</h2>`;
        }
        if (line.startsWith('### ')) {
          return `<h3 class="text-md font-bold my-2">${line.substring(4)}</h3>`;
        }
        // Handle lists
        if (line.match(/^\d+\.\s/)) {
          return `<div class="ml-4">‚Ä¢ ${line.replace(/^\d+\.\s/, '')}</div>`;
        }
        if (line.startsWith('- ')) {
          return `<div class="ml-4">‚Ä¢ ${line.substring(2)}</div>`;
        }
        return line ? `<div>${line}</div>` : '<div>&nbsp;</div>';
      }).join('');
  };

  return (
    <Card className="mb-6">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-xl">
            üéØ Task {task.id}: {task.title}
          </CardTitle>
          <div className="flex gap-2">
            <Badge className={getPriorityBadge(task.priority)}>
              {task.priority.toUpperCase()}
            </Badge>
            <Badge className={getStatusBadge(task.status)}>
              {task.status.toUpperCase()}
            </Badge>
          </div>
        </div>

        {(totalSubtasks > 0 || task.progress) && (
          <div className="space-y-2 mt-4">
            <div className="flex justify-between text-sm">
              <span>Progress</span>
              {totalSubtasks > 0 ? (
                <span>{completedSubtasks}/{totalSubtasks} subtasks</span>
              ) : (
                <span>{Math.round(progress)}%</span>
              )}
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        )}
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Description */}
        <div>
          <h4 className="font-semibold text-gray-300 uppercase text-sm mb-2">üìã Description</h4>
          <div className="text-gray-200 leading-relaxed">
            <div dangerouslySetInnerHTML={{ __html: formatContent(task.description) }} />
          </div>
        </div>

        {/* Implementation Details */}
        <div>
          <h4 className="font-semibold text-gray-300 uppercase text-sm mb-2">üìú Implementation Details</h4>
          <div className="bg-gray-800 p-4 rounded-lg">
            <div 
              className="text-sm text-gray-300 whitespace-pre-wrap"
              dangerouslySetInnerHTML={{ __html: formatContent(task.details) }}
            />
          </div>
        </div>

        {/* Test Strategy */}
        <div>
          <h4 className="font-semibold text-gray-300 uppercase text-sm mb-2">üß™ Test Strategy</h4>
          <div className="bg-gray-800 p-4 rounded-lg">
            <div 
              className="text-sm text-gray-300 whitespace-pre-wrap"
              dangerouslySetInnerHTML={{ __html: formatContent(task.testStrategy) }}
            />
          </div>
        </div>

        {/* Dependencies */}
        {task.dependencies && task.dependencies.length > 0 && (
          <div>
            <h4 className="font-semibold text-gray-300 uppercase text-sm mb-2">üîó Dependencies</h4>
            <div className="bg-gray-800 p-4 rounded-lg">
              <div className="text-sm text-gray-300">
                This task depends on: {task.dependencies.map(dep => (
                  <Badge key={dep} variant="outline" className="ml-1">Task {dep}</Badge>
                ))}
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default TaskPanel;
```

