Create a `components/NavigationSidebar.tsx` file with the following implementation:

```tsx
'use client';

import React from 'react';
import { Task } from '@/types';

interface NavigationSidebarProps {
  tasks: Task[];
  currentTaskIndex: number;
  onTaskSelect: (index: number) => void;
}

const NavigationSidebar: React.FC<NavigationSidebarProps> = ({
  tasks,
  currentTaskIndex,
  onTaskSelect,
}) => {
  if (tasks.length === 0) {
    return (
      <div className="p-4 text-gray-400">
        No tasks available. Please select a JSON file to view tasks.
      </div>
    );
  }

  const currentTask = tasks[currentTaskIndex];

  // Find parent task if current task is a subtask
  const findParentTask = () => {
    // This would be implemented if we had a parent-child relationship in our data model
    // For now, we'll just return the current task
    return currentTask;
  };

  // Get task dependencies
  const getDependencies = () => {
    if (!currentTask.dependencies || currentTask.dependencies.length === 0) {
      return [];
    }

    return currentTask.dependencies.map(depId => {
      const depTask = tasks.find(t => t.id === depId);
      return {
        id: depId,
        title: depTask?.title || `Unknown Task (ID: ${depId})`,
        status: depTask?.status || 'unknown',
        index: tasks.findIndex(t => t.id === depId)
      };
    });
  };

  // Get tasks that depend on the current task
  const getDependents = () => {
    return tasks
      .filter(task => task.dependencies?.includes(currentTask.id))
      .map(task => ({
        id: task.id,
        title: task.title,
        status: task.status,
        index: tasks.findIndex(t => t.id === task.id)
      }));
  };

  // Get status icon
  const getStatusIcon = (status: string) => {
    const icons: Record<string, string> = {
      'done': '‚úÖ',
      'in-progress': 'üü°',
      'pending': '‚è≥',
      'cancelled': '‚ùå',
      'unknown': '‚ùì'
    };
    return icons[status.toLowerCase()] || icons.unknown;
  };

  // Find related tasks (tasks in the same group)
  const getRelatedTasks = () => {
    // This is a simplified implementation
    // In a real app, you might group tasks by feature, component, etc.
    const currentId = currentTask.id;
    const prefix = String(currentId).split('.')[0];

    return tasks
      .filter(task => {
        const taskId = String(task.id);
        return taskId.startsWith(prefix) && taskId !== String(currentId);
      })
      .map(task => ({
        id: task.id,
        title: task.title,
        status: task.status,
        index: tasks.findIndex(t => t.id === task.id)
      }));
  };

  const dependencies = getDependencies();
  const dependents = getDependents();
  const relatedTasks = getRelatedTasks();
  const parentTask = findParentTask();

  // Render a task item with status icon
  const renderTaskItem = (task: { id: number, title: string, status: string, index: number }) => (
    <div 
      key={task.id}
      className="flex items-center p-2 hover:bg-gray-700 rounded cursor-pointer"
      onClick={() => task.index >= 0 ? onTaskSelect(task.index) : null}
    >
      <span className="mr-2">{getStatusIcon(task.status)}</span>
      <span className="truncate">
        Task {task.id}: {task.title.length > 30 ? `${task.title.substring(0, 30)}...` : task.title}
      </span>
    </div>
  );

  return (
    <div className="h-full overflow-y-auto">
      <div className="p-4">
        <h3 className="text-lg font-medium mb-4">üìÇ TASK HIERARCHY</h3>

        {/* Current Task */}
        <div className="mb-6">
          <div className="text-sm text-gray-400 mb-1">CURRENT TASK</div>
          <div className="bg-gray-700 p-2 rounded">
            <div className="flex items-center">
              <span className="mr-2">{getStatusIcon(currentTask.status)}</span>
              <span className="font-medium">
                Task {currentTask.id}: {currentTask.title.length > 25 ? `${currentTask.title.substring(0, 25)}...` : currentTask.title}
              </span>
            </div>
          </div>
        </div>

        {/* Dependencies */}
        {dependencies.length > 0 && (
          <div className="mb-6">
            <div className="text-sm text-gray-400 mb-1">üîó DEPENDENCIES</div>
            <div className="bg-gray-800 rounded">
              {dependencies.map(renderTaskItem)}
            </div>
          </div>
        )}

        {/* Dependents */}
        {dependents.length > 0 && (
          <div className="mb-6">
            <div className="text-sm text-gray-400 mb-1">‚è≠Ô∏è DEPENDENTS</div>
            <div className="bg-gray-800 rounded">
              {dependents.map(renderTaskItem)}
            </div>
          </div>
        )}

        {/* Related Tasks */}
        {relatedTasks.length > 0 && (
          <div className="mb-6">
            <div className="text-sm text-gray-400 mb-1">üîÑ RELATED TASKS</div>
            <div className="bg-gray-800 rounded">
              {relatedTasks.map(renderTaskItem)}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default NavigationSidebar;
```

