```tsx
'use client';

import React, { useState, useEffect } from 'react';
import Header from './Header';
import TaskNavigationBar from './TaskNavigationBar';
import ContextSelector from './ContextSelector';
import TaskPanel from './TaskPanel';
import NavigationSidebar from './NavigationSidebar';
import { TaskData } from '@/types';

const TaskMonitor: React.FC = () => {
  // Application state
  const [taskData, setTaskData] = useState<TaskData | null>(null);
  const [currentContext, setCurrentContext] = useState<string>('master'); // Default context
  const [currentTaskIndex, setCurrentTaskIndex] = useState<number>(0);
  const [isAutoRefresh, setIsAutoRefresh] = useState<boolean>(true);
  const [connectionStatus, setConnectionStatus] = useState<'live' | 'offline' | 'error'>('offline');
  const [selectedFile, setSelectedFile] = useState<string>('No file selected');
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filteredTasks, setFilteredTasks] = useState<any[]>([]);

  // Get current tasks based on context
  const getCurrentTasks = () => {
    if (!taskData || !taskData[currentContext]) {
      return [];
    }

    const tasks = taskData[currentContext].tasks;

    if (!searchQuery) {
      return tasks;
    }

    // Filter tasks based on search query
    return tasks.filter(task => 
      task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      task.description.toLowerCase().includes(searchQuery.toLowerCase())
    );
  };

  // Effect for auto-refresh
  useEffect(() => {
    if (!isAutoRefresh) return;

    const fetchData = async () => {
      try {
        const response = await fetch('/api/tasks');
        if (response.ok) {
          const result = await response.json();
          setTaskData(result.data);
          setConnectionStatus('live');
        } else if (response.status === 404) {
          setConnectionStatus('offline');
        } else {
          setConnectionStatus('error');
        }
      } catch (error) {
        console.error('Error fetching task data:', error);
        setConnectionStatus('error');
      }
    };

    // Initial fetch
    fetchData();

    // Set up polling interval
    const interval = setInterval(fetchData, 5000);

    return () => clearInterval(interval);
  }, [isAutoRefresh]);

  // Effect for search filtering
  useEffect(() => {
    setFilteredTasks(getCurrentTasks());
  }, [searchQuery, taskData, currentContext]);

  // Handle file upload
  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('tasks', file);

    try {
      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setSelectedFile(file.name);
        setConnectionStatus('live');

        // Immediately fetch the uploaded data
        const tasksResponse = await fetch('/api/tasks');
        if (tasksResponse.ok) {
          const result = await tasksResponse.json();
          setTaskData(result.data);

          // Reset to first task in the default context
          if (result.data && Object.keys(result.data).length > 0) {
            const firstContext = Object.keys(result.data)[0];
            setCurrentContext(firstContext);
            setCurrentTaskIndex(0);
          }
        }
      } else {
        setConnectionStatus('error');
        alert('Error uploading file. Please ensure it\'s a valid JSON file.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      setConnectionStatus('error');
      alert('Error uploading file.');
    }
  };

  // Toggle auto-refresh
  const toggleAutoRefresh = () => {
    setIsAutoRefresh(!isAutoRefresh);
  };

  // Handle context selection
  const handleContextSelect = (context: string) => {
    setCurrentContext(context);
    setCurrentTaskIndex(0); // Reset to first task in the new context
  };

  // Handle search
  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  // Handle settings
  const handleOpenSettings = () => {
    alert('Settings functionality would be implemented here.');
  };

  // Get current tasks
  const currentTasks = filteredTasks;
  const currentTask = currentTasks[currentTaskIndex];

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 flex flex-col">
      {/* Header */}
      <Header
        selectedFile={selectedFile}
        isAutoRefresh={isAutoRefresh}
        connectionStatus={connectionStatus}
        onFileSelect={handleFileUpload}
        onToggleAutoRefresh={toggleAutoRefresh}
        onOpenSettings={handleOpenSettings}
      />

      {/* Context Selector */}
      <ContextSelector
        taskData={taskData}
        currentContext={currentContext}
        onContextSelect={handleContextSelect}
      />

      {/* Task Navigation */}
      <TaskNavigationBar
        tasks={currentTasks}
        currentTaskIndex={currentTaskIndex}
        onTaskSelect={setCurrentTaskIndex}
        onSearch={handleSearch}
      />

      {/* Main Content */}
      <div className="flex flex-1 overflow-hidden">
        {/* Main Panel */}
        <div className="flex-1 p-6 overflow-y-auto">
          <TaskPanel task={currentTask} />
        </div>

        {/* Sidebar */}
        <div className="w-80 border-l border-gray-700 bg-gray-800 overflow-y-auto">
          <NavigationSidebar
            tasks={taskData?.[currentContext]?.tasks || []}
            currentTaskIndex={currentTaskIndex}
            onTaskSelect={setCurrentTaskIndex}
          />
        </div>
      </div>
    </div>
  );
};

export default TaskMonitor;
```

